import React from 'react';
import './SearchBar.css';


class SearchBar extends React.Component{
    constructor(props){
        super(props);
        this.state = {term: '', location: '', sortBy: 'best_match'};
        this.handleLocationChange = this.handleLocationChange.bind(this);
        this.handleTermChange = this.handleTermChange.bind(this);
        this.sortByOptions = {
            "Best Match": "best_match",
            "Highest Rated": "rating",
            "Most Reviewed": "review_count"
            }
    }

    //class to sort options
    getSortByClass(sortByOption){
        if(this.state.sortBy === sortByOption){
            return 'active';
        }else{
            return '';
        }
    }
    //event handler for sort changes
    handleSortByChange(sortByOption){
        this.setState({
            sortBy: sortByOption
        });
    }
    //handle busniess change
    handleTermChange(e){
        this.setState({term: e.target.value});
    }
    //handle location change
    handleLocationChange(e){
        this.setState({location: e.target.value});
    }
//get the css class, render the sorted option by user by calling the event handler
    renderSortByOptions(){
        return Object.keys(this.sortByOptions).map(
            sortByOption => {
                let sortByOptionsValue = this.sortByOptions[sortByOption];
                return <li className={this.getSortByClass(sortByOptionsValue)} 
                        onClick={this.handleSortByChange.bind(this, sortByOptionsValue)}
                key={sortByOptionsValue}>{sortByOption}</li>
            }
        );
    }
    //search the yelp api
    searchYelp(term, location, sortBy){
        console.log(`searching for ${term}, ${location}, ${sortBy}`);
    }
    render(){
        return (
            <div className="SearchBar">
            <div className="SearchBar-sort-options">
                <ul>
                {this.renderSortByOptions()}
                </ul>
            </div>
            <div className="SearchBar-fields">
                <input onChange={this.handleTermChange} placeholder="Search Businesses" />
                <input onChange={this.handleLocationChange} placeholder="Where?" />
            </div>
            <div className="SearchBar-submit">
                <a>Let's Go</a>
            </div>
            </div>
        )
    }
}
export default SearchBar;
