import React from 'react';
import './App.css';
import SearchBar from './../SearchBar/SearchBar';
import SearchResults from './../SearchResults/SearchResults';
import Playlist from './../Playlist/Playlist';

class App extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      searchResults : [
        {
          name: "Test",
          artist: 'Test Artist',
          album: 'Album one',
          id: 1
      },
      {
          name: "Test1",
          artist: 'Test Artist1',
          album: 'Album one',
          id: 2
      },
      {
          name: "Test2",
          artist: 'Test Artist2',
          album: 'Album one',
          id: 3
      }
      ],

      playlistName: 'Last Show',
      playlistTracks: [
        {name: "Playlist name",
      artist: 'Playlist Test Artist',
      album: 'Playlist Album one',
      id: 4},

      {name: "Playlist 1 name",
      artist: 'Playlist 1 Test Artist',
      album: 'Playlist 1 Album one',
      id: 5}, 

      {name: "Playlist 2 name",
      artist: 'Playlist 2 Test Artist',
      album: 'Playlist 2 Album one',
      id: 6}
    ]
    };
    this.addTrack = this.addTrack.bind(this);

  }
//add track
  addTrack(track){
    let tracks = this.state.playlistTracks;
      if(tracks.find(SavedTrack =>
        SavedTrack.id === track.id)) {
        return;
      }
      tracks.push(track);
      this.setState({playlistTracks : tracks});
  }
  render(){
    return (
      <div>
        <h1>Ja<span className="highlight">mmm</span>ing</h1>
        <div className="App">
        <SearchBar />
        <div className="App-playlist">
         <SearchResults searchResults={this.state.searchResults} onAdd={this.addTrack}/>
        <Playlist playlist={this.state.playlistName} playlistTracks={this.state.playlistTracks}/>
        </div>
        </div>
      </div>
    )
  }
}

export default App;
